package corejava_solving_Qspiders;

public class Condition3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Question 3 write a condition to check weather the given number is even and present between 40 & 70
		int num = 50;//taking an variable as num of int data type and storing the value 50
		boolean res = ((num%2==0)&&(num>40&&num<=70));//written condition and storing in a variable named as res which is boolean type
		System.out.println(res);//printing the result

	}

}
